          term={props.keyword}
          onChange={(event) => {
            props.setKeyword(event.target.value);
          }}
        />{" "}
      </Form.Group>
      <Button variant="primary" type="submit">
        {" "}
        Submit
      </Button>{" "}
    </Form>
  );
};

export default Search;
