import React from "react";
import PropTypes from "prop-types";

const Book = (props) => {
  // let {
  //   id,
  //   volumeInfo: { title, authors, description },
  //   saleInfo: {
  //     listPrice: { amount },
  //   },
  // } = book;

  const info = props.book.volumeInfo;

  const {
    volumeInfo: {
      title,
      authors,
      description,
      imageLinks: { thumbnail },
    },
  } = props.book;

  const renderAmount = () => {
    if (
      props.book.saleInfo &&
      props.book.saleInfo.listPrice &&
      props.book.saleInfo.listPrice.amount
    ) {
      return "£" + props.book.saleInfo.listPrice.amount;
    }
    return;
  };

  const renderAuthors = () => {
    if (authors.length === 1) {
      return authors;
    }
    return authors.map((author) => author + ", ");
  };

  return (
    <div>
      <img src={thumbnail} />
      <h2>
        {title} - {renderAuthors()}
      </h2>
      <p>{renderAmount()}</p>
      <p> {description}</p>
    </div>
  );
};

Book.propTypes = {
  book: PropTypes.shape({
    volumeInfo: PropTypes.shape({
      title: PropTypes.string.isRequired,
    }),
  }),
};

Book.defaultProps = {
  title: "This is title",
};

export default Book;
