  // }

  // if (books.length === 0) {
  //   return "No books found";
  // }

  async function findBooks(term) {
    const results = await fetch(
      `https://www.googleapis.com/books/v1/volumes?q=${term}&filter=paid-ebooks&print- type=books&projection=lite`
    ).then((res) => res.json());
    if (!results.error) {
      setBooks(results.items);
    }
  }

  return (
    <Router>
      <Route
        exact
        path="/"
        render={() => (
          <React.Fragment>
            <Header />
            <Search
              findBooks={findBooks}
              keyword={keyword}
              setKeyword={setKeyword}
            />

            <BookList books={books} addBook={addBook} />
          </React.Fragment>
        )}
      />

      <Route
        exact
        path="/bookcase"
        render={() => (
          <React.Fragment>
            <Header />
            <BookList books={bookcase} />
          </React.Fragment>
        )}
      />

      <Route
        exact
        path="/about"
        render={() => (
          <React.Fragment>
            <Header />
            <About />
          </React.Fragment>
        )}
      />
    </Router>
  );
};

export default App;
